src/components/app/application-dialog.ts.content:
'use client';

import { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { ApplicationForm } from './application-form';
import { Button } from '../ui/button';

export function ApplicationDialog() {
  const [open, setOpen] = useState(false);

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {/* This is a hidden trigger that we can programmatically click */}
        <Button id="hidden-apply-trigger" className="hidden" />
      </DialogTrigger>
      <DialogContent className="sm:max-w-[800px]">
        <DialogHeader>
          <DialogTitle>Apply Now</DialogTitle>
        </DialogHeader>
        <ApplicationForm onFormSubmit={() => setOpen(false)} />
      </DialogContent>
    </Dialog>
  );
}

// Helper to add click listeners to all apply buttons
if (typeof window !== 'undefined') {
  document.addEventListener('click', (event) => {
    const target = event.target as HTMLElement;
    if (target.closest('[data-apply-btn]')) {
      const trigger = document.getElementById('hidden-apply-trigger');
      if (trigger) {
        trigger.click();
      }
    }
  });
}

src/components/app/application-form.tsx.content:
'use client';

import { useState, useEffect } from 'react';
import { useForm, useWatch } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Checkbox } from '@/components/ui/checkbox';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { useToast } from '@/hooks/use-toast';
import { submitApplication, type SubmitResult } from '@/app/actions';
import { type ApplicationData, applicationSchema } from '@/lib/schemas';
import { Loader2 } from 'lucide-react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '../ui/textarea';


export function ApplicationForm({ onFormSubmit }: { onFormSubmit?: () => void }) {
  const [isTermsRead, setIsTermsRead] = useState(false);
  const [isTermsModalOpen, setIsTermsModalOpen] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submissionResult, setSubmissionResult] = useState<SubmitResult | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    if (submissionResult?.success && submissionResult.applicationId) {
      const timer = setTimeout(() => {
        setSubmissionResult(null);
        if (onFormSubmit) {
          onFormSubmit();
        }
      }, 30000);
      return () => clearTimeout(timer);
    }
  }, [submissionResult, onFormSubmit]);

  const form = useForm<ApplicationData>({
    resolver: zodResolver(applicationSchema),
    defaultValues: {
      name: '',
      discordTag: '',
      email: '',
      steamUrl: '',
      terms: false,
      friendsMention: '',
      othersMention: '',
    },
  });

  const howYouFoundValue = useWatch({
    control: form.control,
    name: 'howYouFound',
  });


  async function onSubmit(data: ApplicationData) {
    setIsSubmitting(true);
    const result = await submitApplication(data);
    setIsSubmitting(false);

    if (result.success) {
      setSubmissionResult(result);
      form.reset();
      setIsTermsRead(false);
    } else {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: result.message || 'An error occurred',
      });
    }
  }

  if (submissionResult?.success) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-primary">Application Submitted!</h2>
        <p className="text-muted-foreground mt-2">Thank you for applying.</p>
        <p className="mt-4">Your Application ID is:</p>
        <p className="text-3xl font-bold text-primary mt-2">{submissionResult.applicationId}</p>
        <p className="text-sm text-muted-foreground mt-4">You can close this window. It will close automatically in 30 seconds.</p>
      </div>
    );
  }

  return (
    <>
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Full Name</FormLabel>
                  <FormControl>
                    <Input placeholder="Enter your full name" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="discordTag"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Discord Tag</FormLabel>
                  <FormControl>
                    <Input placeholder="e.g., username#1234" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Email Address</FormLabel>
                  <FormControl>
                    <Input
                      type="email"
                      placeholder="your.email@example.com"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="steamUrl"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Steam Profile URL</FormLabel>
                  <FormControl>
                    <Input placeholder="https://steamcommunity.com/id/yourprofile" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              control={form.control}
              name="experience"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Your Experience</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select your experience level" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="fresher">Fresher</SelectItem>
                      <SelectItem value="experienced">Experienced</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="howYouFound"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>How did you find our VTC?</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select an option" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="truckersmp">TruckersMP</SelectItem>
                      <SelectItem value="friends">Friends</SelectItem>
                      <SelectItem value="others">Others</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          {howYouFoundValue === 'friends' && (
             <FormField
                control={form.control}
                name="friendsMention"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Friend's Name(s)</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Please mention the friend(s) who referred you."
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
          )}

          {howYouFoundValue === 'others' && (
             <FormField
                control={form.control}
                name="othersMention"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Please Specify</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="e.g., Discord, TruckersMP forums, etc."
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
          )}

          <FormField
            control={form.control}
            name="terms"
            render={({ field }) => (
              <FormItem className="flex flex-row items-start space-x-3 space-y-0 rounded-md border p-4">
                <FormControl>
                  <Checkbox
                    checked={field.value}
                    onCheckedChange={field.onChange}
                    disabled={!isTermsRead || isSubmitting}
                  />
                </FormControl>
                <div className="space-y-1 leading-none">
                  <FormLabel>
                    I agree to the terms and conditions.
                  </FormLabel>
                  <Button
                    type="button"
                    variant="link"
                    className="p-0 h-auto text-primary"
                    onClick={() => setIsTermsModalOpen(true)}
                  >
                    Click here to read the terms.
                  </Button>
                  <FormMessage />
                </div>
              </FormItem>
            )}
          />
          <Button type="submit" className="w-full rounded-full" size="lg" disabled={isSubmitting}>
            {isSubmitting ? <><Loader2 className="mr-2 h-4 w-4 animate-spin" /> Submitting...</> : 'Submit Application'}
          </Button>
        </form>
      </Form>

      <AlertDialog open={isTermsModalOpen} onOpenChange={setIsTermsModalOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Terms and Conditions</AlertDialogTitle>
            <AlertDialogDescription className="text-left max-h-[60vh] overflow-y-auto pr-2">
                <p className="mb-4">Welcome to Tamil Pasanga VTC. By submitting this application, you agree to the following terms and conditions:</p>
                <ol className="list-decimal list-inside space-y-2">
                    <li>You must be at least 15 years of age to apply.</li>
                    <li>You must own a legal copy of Euro Truck Simulator 2 or American Truck Simulator on Steam.</li>
                    <li>You must have a registered TruckersMP account in good standing.</li>
                    <li>Respect all members of the community, including staff and fellow drivers. Harassment, discrimination, or any form of abuse will not be tolerated.</li>
                    <li>Follow all TruckersMP rules and regulations during convoys and on public servers.</li>
                    <li>Maintain a professional and realistic driving standard. Reckless driving is not permitted.</li>
                    <li>You are required to log a minimum number of jobs/miles per month as specified in our driver handbook to remain an active driver.</li>
                    <li>Communication is key. You must join our Discord server and be responsive to official announcements.</li>
                    <li>Dual VTCing (being a member of another VTC) is permitted, but your commitment to Tamil Pasanga must be maintained.</li>
                    <li>Failure to comply with these rules may result in disciplinary action, including suspension or removal from the VTC.</li>
                </ol>
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogAction
              onClick={() => {
                setIsTermsRead(true);
                setIsTermsModalOpen(false);
              }}
            >
              I have read and agree
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

src/components/app/application-status.ts.content:
'use client';

import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Loader2, CheckCircle, XCircle, AlertCircle, Clock } from 'lucide-react';
import { getApplicationStatus, type ApplicationStatusResult } from '@/app/actions';
import { Badge } from '@/components/ui/badge';

const applicationStatusSchema = z.object({
  applicationId: z.string().regex(/^TP-\d{4}$/, 'Invalid Application ID format. Example: TP-1234'),
});

type ApplicationStatusFormData = z.infer<typeof applicationStatusSchema>;

const statusInfo = {
    Accepted: {
        icon: <CheckCircle className="h-12 w-12 text-green-500" />,
        badge: <Badge variant="default" className="bg-green-500">Accepted</Badge>,
        message: 'Congratulations! Your application has been accepted.',
    },
    Pending: {
        icon: <Clock className="h-12 w-12 text-yellow-500" />,
        badge: <Badge variant="secondary" className="bg-yellow-500">Pending</Badge>,
        message: 'Your application is currently under review. Please check back later.',
    },
    Rejected: {
        icon: <XCircle className="h-12 w-12 text-red-500" />,
        badge: <Badge variant="destructive">Rejected</Badge>,
        message: 'We regret to inform you that your application has been rejected.',
    },
    Interview: {
        icon: <AlertCircle className="h-12 w-12 text-blue-500" />,
        badge: <Badge className="bg-blue-500">Interview</Badge>,
        message: 'Your application has passed the initial screening. Please check your email for interview details.',
    },
    'Not Found': {
        icon: <AlertCircle className="h-12 w-12 text-muted-foreground" />,
        badge: <Badge variant="outline">Not Found</Badge>,
        message: 'No application found with this ID. Please double-check your Application ID.',
    },
};

export function ApplicationStatus() {
  const [isLoading, setIsLoading] = useState(false);
  const [statusResult, setStatusResult] = useState<ApplicationStatusResult | null>(null);

  const form = useForm<ApplicationStatusFormData>({
    resolver: zodResolver(applicationStatusSchema),
    defaultValues: {
      applicationId: '',
    },
  });

  const onSubmit = async (data: ApplicationStatusFormData) => {
    setIsLoading(true);
    setStatusResult(null);
    const result = await getApplicationStatus(data.applicationId);
    setStatusResult(result);
    setIsLoading(false);
  };

  return (
    <div className="container mx-auto px-4">
      <Card className="max-w-2xl mx-auto">
        <CardHeader>
          <CardTitle>Check Application Status</CardTitle>
          <CardDescription>Enter your application ID to see the current status of your application.</CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              <FormField
                control={form.control}
                name="applicationId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Application ID</FormLabel>
                    <FormControl>
                      <Input placeholder="e.g., TP-1234" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <Button type="submit" disabled={isLoading} className="w-full">
                {isLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                Check Status
              </Button>
            </form>
          </Form>

          {statusResult && (
            <div className="mt-8 text-center border-t pt-8">
              <div className="flex justify-center mb-4">{statusInfo[statusResult.status].icon}</div>
              <h3 className="text-xl font-semibold mb-2">Application ID: {statusResult.applicationId}</h3>
              <div className="mb-4 flex justify-center">{statusInfo[statusResult.status].badge}</div>
              <p className="text-muted-foreground">{statusInfo[statusResult.status].message}</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

