export function UpdateApplicationStatus({
  applicationId,
  status,
  currentStatus,
}: {
  applicationId: string;
  status: ApplicationStatus;
  currentStatus: ApplicationStatus;
}) {
  const [isPending, startTransition] = useTransition();
  const [selectedRole, setSelectedRole] = useState('Trainee');
  const { toast } = useToast();
  const config = statusConfig[status];

  const handleUpdate = (role?: string) => {
    startTransition(async () => {
      const result = await updateStatusAction(applicationId, status, role);
      if (result.success) {
        toast({ title: 'Success', description: 'Application status updated.' });
      } else {
        toast({ variant: 'destructive', title: 'Error', description: result.message });
      }
    });
  };

  const isDisabled = isPending || currentStatus === status;


  if (status === 'Accepted') {
    return (
       <DropdownMenuSub>
        <DropdownMenuSubTrigger
          disabled={isDisabled}
          className={config.className}
        >
          {isPending ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : config.icon}
          {config.label}
        </DropdownMenuSubTrigger>
        <DropdownMenuPortal>
            <DropdownMenuSubContent>
                <DropdownMenuRadioGroup value={selectedRole} onValueChange={setSelectedRole}>
                    {roleOptions.map((role) => (
                        <DropdownMenuRadioItem key={role} value={role}>
                            {role}
                        </DropdownMenuRadioItem>
                    ))}
                </DropdownMenuRadioGroup>
                <DropdownMenuItem
                    onClick={() => handleUpdate(selectedRole)}
                    className="mt-2 justify-center bg-primary text-primary-foreground hover:bg-primary/90 focus:bg-primary focus:text-primary-foreground"
                >
                    Confirm Acceptance
                </DropdownMenuItem>
            </DropdownMenuSubContent>
        </DropdownMenuPortal>
      </DropdownMenuSub>
    );
  }


  return (
    <DropdownMenuItem
      onClick={() => handleUpdate()}
      disabled={isDisabled}
      className={config.className}
      onSelect={(e) => e.preventDefault()}
    >
      {isPending ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : config.icon}
      {config.label}
    </DropdownMenuItem>
  );
}

export function UpdateBookingStatus({
    eventId,
    areaId,
    bookingId,
    newStatus,
}: {
    eventId: string;
    areaId: string;
    bookingId: string;
    newStatus: 'approved' | 'rejected' | 'hold';
}) {
    const [isPending, startTransition] = useTransition();
    const { toast } = useToast();

    let icon, label, className;

    switch (newStatus) {
        case 'approved':
            icon = <CheckCircle className="mr-2 h-4 w-4 text-green-500" />;
            label = 'Approve';
            className = 'text-green-500';
            break;
        case 'rejected':
            icon = <XCircle className="mr-2 h-4 w-4 text-red-500" />;
            label = 'Reject';
            className = 'text-red-500';
            break;
        case 'hold':
            icon = <PauseCircle className="mr-2 h-4 w-4 text-yellow-500" />;
            label = 'Put on Hold';
            className = 'text-yellow-500';
            break;
    }


    const handleUpdate = () => {
        startTransition(async () => {
            const result = await updateBookingAction(eventId, areaId, bookingId, newStatus);
            if (result.success) {
                toast({ title: 'Success', description: result.message });
            } else {
                toast({ variant: 'destructive', title: 'Error', description: result.message });
            }
        });
    };

    return (
        <DropdownMenuItem
            onClick={handleUpdate}
            disabled={isPending}
            className={className}
            onSelect={(e) => e.preventDefault()}
        >
            {isPending ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : icon}
            {label}
        </DropdownMenuItem>
    );
}

src/app/admin/applications/page.tsx.content:
'use client';

import React, { useState, useEffect } from 'react';
import { Footer } from "@/components/app/footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import type { Application } from "@/lib/applications";
import { CheckCircle, Clock, FileText, MoreHorizontal, XCircle, AlertCircle, ChevronDown, ChevronUp, PauseCircle } from "lucide-react";
import Link from "next/link";
import { Badge } from "@/components/ui/badge";
import { DropdownMenu, DropdownMenuContent, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { format } from 'date-fns';
import { UpdateApplicationStatus, UpdateBookingStatus } from "./actions";
import { getApplications, getEventsWithBookings } from './server-actions';
import { Skeleton } from '@/components/ui/skeleton';
import type { Event } from '@/lib/events';

const statusInfo = {
    Accepted: { icon: <CheckCircle className="h-4 w-4 text-green-500" />, badge: <Badge variant="default" className="bg-green-500">Accepted</Badge> },
    Pending: { icon: <Clock className="h-4 w-4 text-yellow-500" />, badge: <Badge variant="secondary" className="bg-yellow-500">Pending</Badge> },
    Rejected: { icon: <XCircle className="h-4 w-4 text-red-500" />, badge: <Badge variant="destructive">Rejected</Badge> },
    Interview: { icon: <AlertCircle className="h-4 w-4 text-blue-500" />, badge: <Badge className="bg-blue-500">Interview</Badge> },
};

const bookingStatusInfo = {
    approved: { badge: <Badge className='bg-green-500'>Approved</Badge> },
    pending: { badge: <Badge className='bg-yellow-500'>Pending</Badge> },
    rejected: { badge: <Badge className='bg-red-500'>Rejected</Badge> },
    hold: { badge: <Badge className='bg-orange-500'>On Hold</Badge> },
};


function ApplicationRow({ app }: { app: Application }) {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <React.Fragment>
            <TableRow>
                <TableCell>
                    <Button variant="ghost" size="icon" onClick={() => setIsOpen(!isOpen)} className="group">
                        <ChevronDown className={isOpen ? "hidden" : "h-4 w-4"} />
                        <ChevronUp className={isOpen ? "h-4 w-4" : "hidden"} />
                    </Button>
                </TableCell>
                <TableCell className="font-medium">{app.id}</TableCell>
                <TableCell>{app.name}</TableCell>
                <TableCell>{app.discordTag}</TableCell>
                <TableCell>{format(new Date(app.submittedAt), 'PPp')}</TableCell>
                <TableCell>{statusInfo[app.status]?.badge || app.status}</TableCell>
                <TableCell className="text-right">
                    <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                            <Button variant="ghost" className="h-8 w-8 p-0">
                            <span className="sr-only">Open menu</span>
                            <MoreHorizontal className="h-4 w-4" />
                            </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                            <UpdateApplicationStatus applicationId={app.id} status="Accepted" currentStatus={app.status} />
                            <UpdateApplicationStatus applicationId={app.id} status="Rejected" currentStatus={app.status} />
                            <UpdateApplicationStatus applicationId={app.id} status="Interview" currentStatus={app.status} />
                        </DropdownMenuContent>
                    </DropdownMenu>
                </TableCell>
            </TableRow>
            {isOpen && (
                <TableRow className="bg-muted/50">
                    <TableCell colSpan={7} className="p-4">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                            <div><strong>Email:</strong> {app.email}</div>
                            <div><strong>Experience:</strong> <span className="capitalize">{app.experience}</span></div>
                            <div>
                                <strong>Steam Profile:</strong>
                                <Link href={app.steamUrl} target="_blank" className="text-primary hover:underline ml-2">View Profile</Link>
                            </div>
                            <div className="col-span-full">
                                <strong>How they found us:</strong> <span className="capitalize">{app.howYouFound}</span>
                                {app.howYouFound === 'friends' && app.friendsMention && ` - ${app.friendsMention}`}
                                {app.howYouFound === 'others' && app.othersMention && ` - ${app.othersMention}`}
                            </div>
                        </div>
                    </TableCell>
                </TableRow>
            )}
        </React.Fragment>
    );
}

function BookingRow({ event, area, booking }: { event: Event; area: NonNullable<Event['slots']>[0]; booking: NonNullable<NonNullable<Event['slots']>[0]['bookings']>[0] }) {
  return (
     <TableRow>
        <TableCell></TableCell>
        <TableCell className="font-medium">{booking.id}</TableCell>
        <TableCell>{booking.vtcName}</TableCell>
        <TableCell>{event.title}</TableCell>
        <TableCell>Slot #{booking.slotNumber} ({area.areaName})</TableCell>
        <TableCell>{bookingStatusInfo[booking.status]?.badge || booking.status}</TableCell>
        <TableCell className="text-right">
             <DropdownMenu>
                <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="h-8 w-8 p-0" disabled={booking.status === 'approved' || booking.status === 'rejected'}>
                    <span className="sr-only">Open menu</span>
                    <MoreHorizontal className="h-4 w-4" />
                    </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                    <UpdateBookingStatus eventId={event.id} areaId={area.id} bookingId={booking.id} newStatus="approved" />
                    <UpdateBookingStatus eventId={event.id} areaId={area.id} bookingId={booking.id} newStatus="rejected" />
                    <UpdateBookingStatus eventId={event.id} areaId={area.id} bookingId={booking.id} newStatus="hold" />
                </DropdownMenuContent>
            </DropdownMenu>
        </TableCell>
    </TableRow>
  );
}


export default function ApplicationsAdminPage() {
    const [applications, setApplications] = useState<Application[]>([]);
    const [bookingEvents, setBookingEvents] = useState<Event[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function loadData() {
            setIsLoading(true);
            try {
                const [fetchedApplications, fetchedEvents] = await Promise.all([
                    getApplications(),
                    getEventsWithBookings()
                ]);
                setApplications(fetchedApplications);
                setBookingEvents(fetchedEvents);
            } catch (error) {
                console.error("Failed to load data:", error);
            } finally {
                setIsLoading(false);
            }
        }
        loadData();
    }, []);

    const allBookings = bookingEvents.flatMap(event => 
        event.slots?.flatMap(area => 
            area.bookings?.map(booking => ({ event, area, booking })) || []
        ) || []
    );


    return (
        <div className="flex flex-col min-h-screen bg-background">
            <main className="flex-grow p-4 md:p-8">
                <div className="max-w-7xl mx-auto">
                    <div className="flex items-center justify-between mb-8">
                        <h1 className="text-3xl font-headline flex items-center gap-2">
                            <FileText />
                            Manage Applications & Bookings
                        </h1>
                    </div>

                    <Card>
                        <CardHeader>
                            <CardTitle>All Applications</CardTitle>
                            <CardDescription>
                                {isLoading ? 'Loading applications...' : `${applications.length} application(s) found.`}
                            </CardDescription>
                        </CardHeader>
                        <CardContent>
                            <Table>
                                <TableHeader>
                                    <TableRow>
                                        <TableHead className="w-12"></TableHead>
                                        <TableHead>ID</TableHead>
                                        <TableHead>Name</TableHead>
                                        <TableHead>Discord Tag</TableHead>
                                        <TableHead>Submitted At</TableHead>
                                        <TableHead>Status</TableHead>
                                        <TableHead className="text-right">Actions</TableHead>
                                    </TableRow>
                                </TableHeader>
                                <TableBody>
                                    {isLoading ? (
                                        Array.from({ length: 5 }).map((_, index) => (
                                            <TableRow key={index}>
                                                <TableCell colSpan={7}>
                                                    <Skeleton className="h-8 w-full" />
                                                </TableCell>
                                            </TableRow>
                                        ))
                                    ) : (
                                        applications.map((app) => (
                                            <ApplicationRow key={app.id} app={app} />
                                        ))
                                    )}
                                </TableBody>
                            </Table>
                        </CardContent>
                    </Card>

                     <Card className="mt-8">
                        <CardHeader>
                            <CardTitle>Slot Booking Requests</CardTitle>
                            <CardDescription>
                                {isLoading ? 'Loading booking requests...' : `${allBookings.length} booking request(s) found.`}
                            </CardDescription>
                        </CardHeader>
                        <CardContent>
                           <Table>
                                <TableHeader>
                                    <TableRow>
                                        <TableHead className="w-12"></TableHead>
                                        <TableHead>Booking ID</TableHead>
                                        <TableHead>VTC Name</TableHead>
                                        <TableHead>Event</TableHead>
                                        <TableHead>Slot</TableHead>
                                        <TableHead>Status</TableHead>
                                        <TableHead className="text-right">Actions</TableHead>
                                    </TableRow>
                                </TableHeader>
                                <TableBody>
                                     {isLoading ? (
                                        Array.from({ length: 3 }).map((_, index) => (
                                            <TableRow key={`booking-skel-${index}`}>
                                                <TableCell colSpan={7}>
                                                    <Skeleton className="h-8 w-full" />
                                                </TableCell>
                                            </TableRow>
                                        ))
                                    ) : allBookings.length > 0 ? (
                                        allBookings.map(({ event, area, booking }) => (
                                            <BookingRow key={booking.id} event={event} area={area} booking={booking} />
                                        ))
                                    ) : (
                                        <TableRow>
                                            <TableCell colSpan={7} className="text-center">No booking requests found.</TableCell>
                                        </TableRow>
                                    )}
                                </TableBody>
                            </Table>
                        </CardContent>
                    </Card>

                     <div className="mt-8 text-center">
                        <Button variant="outline" asChild>
                            <Link href="/admin">Back to Admin</Link>
                        </Button>
                    </div>
                </div>
            </main>
            <Footer />
        </div>
    );
}

src/app/admin/applications/server-action.content:
'use server';

import { revalidatePath } from 'next/cache';
import fs from 'fs/promises';
import path from 'path';
import type { ApplicationStatus, ApplicationsData, Application } from '@/lib/applications';
import type { StaffData, StaffMember } from '@/lib/staff-members';
import type { EventsData, Event, Booking, BookingStatus } from '@/lib/events';

const applicationsFilePath = path.join(process.cwd(), 'src', 'lib', 'applications.json');
const staffFilePath = path.join(process.cwd(), 'src', 'lib', 'staff-members.json');
const eventsFilePath = path.join(process.cwd(), 'src', 'lib', 'events.json');

async function readJsonFile<T>(filePath: string): Promise<T> {
    try {
        const data = await fs.readFile(filePath, 'utf-8');
        return JSON.parse(data);
    } catch (error) {
        if ((error as NodeJS.ErrnoException).code === 'ENOENT') {
            if (filePath.includes('applications.json')) {
                return { applications: [] } as T;
            }
            if (filePath.includes('staff-members.json')) {
                return { staffMembers: [] } as T;
            }
             if (filePath.includes('events.json')) {
                return { events: [] } as T;
            }
        }
        throw error;
    }
}

async function writeJsonFile<T>(filePath: string, data: T): Promise<void> {
    await fs.writeFile(filePath, JSON.stringify(data, null, 2));
}

async function sendApplicationWebhookNotification(application: Application) {
    const webhookUrl = process.env.DISCORD_WEBHOOK_URL;
    if (!webhookUrl) {
        console.error('DISCORD_WEBHOOK_URL is not set.');
        return;
    }

    let title = '';
    let color = 0;
    let description = '';

    switch (application.status) {
        case 'Accepted':
            title = `Application Accepted: ${application.id}`;
            color = 5763719; // Green
            description = `Congratulations to **${application.name}**! Their application has been accepted.`;
            break;
        case 'Rejected':
            title = `Application Rejected: ${application.id}`;
            color = 15548997; // Red
            description = `Application for **${application.name}** has been rejected.`;
            break;
        case 'Interview':
            title = `Application Moved to Interview: ${application.id}`;
            color = 3447003; // Blue
            description = `**${application.name}** has been moved to the interview stage.`;
            break;
        default:
            return; // Don't send for 'Pending' or other statuses
    }

    const embed = {
        title: title,
        description: description,
        color: color,
        timestamp: new Date().toISOString(),
        fields: [
            { name: 'Applicant Name', value: application.name, inline: true },
            { name: 'Discord Tag', value: application.discordTag, inline: true },
        ],
        footer: {
            text: 'Tamil Pasanga VTC | Application Status Update',
        },
    };

    const payload = { embeds: [embed] };

    try {
        const response = await fetch(webhookUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
        });

        if (!response.ok) {
            console.error(`Discord webhook failed with status: ${response.status}`);
        }
    } catch (error) {
        console.error('Error sending Discord webhook notification:', error);
    }
}


export async function getApplications(): Promise<Application[]> {
    const data = await readJsonFile<ApplicationsData>(applicationsFilePath);
    // Sort by submission date, newest first
    return data.applications.sort((a, b) => new Date(b.submittedAt).getTime() - new Date(a.submittedAt).getTime());
}

export async function updateApplicationStatus(
  applicationId: string,
  newStatus: ApplicationStatus,
  role?: string
): Promise<{ success: boolean; message: string }> {
    try {
        const applicationsData = await readJsonFile<ApplicationsData>(applicationsFilePath);
        const staffData = await readJsonFile<StaffData>(staffFilePath);

        const appIndex = applicationsData.applications.findIndex((app) => app.id === applicationId);

        if (appIndex === -1) {
            return { success: false, message: `Application with ID ${applicationId} not found.` };
        }

        const application = applicationsData.applications[appIndex];

        // Update status in applications.json
        application.status = newStatus;
        await writeJsonFile(applicationsFilePath, applicationsData);

        // If accepted, add to staff-members.json if not already present
        if (newStatus === 'Accepted') {
            const isAlreadyStaff = staffData.staffMembers.some(member => member.name === application.name);
            if (!isAlreadyStaff) {
                const newMember: StaffMember = {
                    id: `staff-${Date.now()}`,
                    name: application.name,
                    role: role || 'Trainee',
                    imageId: 'testimonial-avatar',
                    imageUrl: "https://media.discordapp.net/attachments/1116720480544636999/1274425873201631304/TP_NEW_WB_PNGxxxhdpi.png?ex=68d4d8d5&is=68d38755&hm=b6d4e0e4ef2c3215a4de4fb2f592189a60ddd94c651f96fe04deac2e7f96ddc6&=&format=webp&quality=lossless&width=826&height=826",
                    steamUrl: application.steamUrl,
                    truckersmpUrl: "",
                };
                staffData.staffMembers.push(newMember);
                await writeJsonFile(staffFilePath, staffData);
            }
        }

        revalidatePath('/admin/applications');
        revalidatePath('/staff');

        // Send Discord notification
        await sendApplicationWebhookNotification(application);

        return { success: true, message: 'Application status updated successfully.' };
    } catch (error) {
        console.error('Error updating application status:', error);
        return { success: false, message: 'An unexpected error occurred.' };
    }
}

// --- Booking Actions ---

export async function getEventsWithBookings(): Promise<Event[]> {
    const data = await readJsonFile<EventsData>(eventsFilePath);
    return data.events.filter(event => event.slots && event.slots.some(slot => slot.bookings && slot.bookings.length > 0));
}

async function sendBookingWebhookNotification(booking: Booking, event: Event, newStatus: BookingStatus, areaId: string) {
    const webhookUrl = process.env.DISCORD_WEBHOOK_URL;
    if (!webhookUrl) {
        console.error('DISCORD_WEBHOOK_URL is not set.');
        return;
    }

    let title = '';
    let color = 0;
    let description = '';
    let imageUrl = '';

    const area = event.slots?.find(a => a.id === areaId);

    switch (newStatus) {
        case 'approved':
            title = `Booking Approved: ${booking.vtcName}`;
            color = 5763719; // Green
            description = `The booking for **${booking.vtcName}** for slot **#${booking.slotNumber}** at event **${event.title}** has been approved.`;
             if (area) {
                imageUrl = area.imageUrl;
            }
            break;
        case 'rejected':
             title = `Booking Rejected: ${booking.vtcName}`;
            color = 15548997; // Red
            description = `The booking for **${booking.vtcName}** for slot **#${booking.slotNumber}** at event **${event.title}** has been rejected.`;
            break;
        case 'hold':
            title = `Booking On Hold: ${booking.vtcName}`;
            color = 16753920; // Orange
            description = `The booking for **${booking.vtcName}** for slot **#${booking.slotNumber}** at event **${event.title}** has been put on hold.`;
            break;
        default:
            return;
    }


    const embed: any = {
        title: title,
        description: description,
        color: color,
        timestamp: new Date().toISOString(),
        footer: { text: 'Tamil Pasanga VTC | Slot Booking Update' },
    };

    if (newStatus === 'approved' && imageUrl) {
        embed.image = { url: imageUrl };
    }

    try {
        await fetch(webhookUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ embeds: [embed] }),
        });
    } catch (error) {
        console.error('Error sending booking status webhook:', error);
    }
}


export async function updateBookingStatus(
    eventId: string,
    areaId: string,
    bookingId: string,
    newStatus: BookingStatus
): Promise<{ success: boolean; message: string }> {
    try {
        const eventsData = await readJsonFile<EventsData>(eventsFilePath);
        const event = eventsData.events.find(e => e.id === eventId);
        if (!event || !event.slots) return { success: false, message: 'Event not found.' };

        const area = event.slots.find(a => a.id === areaId);
        if (!area || !area.bookings) return { success: false, message: 'Slot area not found.' };

        const booking = area.bookings.find(b => b.id === bookingId);
        if (!booking) return { success: false, message: 'Booking not found.' };

        booking.status = newStatus;

        await writeJsonFile(eventsFilePath, eventsData);

        await sendBookingWebhookNotification(booking, event, newStatus, areaId);

        revalidatePath('/admin/applications');
        revalidatePath(`/events/${eventId}`);

        return { success: true, message: `Booking status updated to ${newStatus}.` };

    } catch (error) {
        console.error('Error updating booking status:', error);
        return { success: false, message: 'An unexpected error occurred.' };
    }
}

src/app/admin/events/actions.ts.content:
'use server';

import fs from 'fs/promises';
import path from 'path';
import { revalidatePath } from 'next/cache';
import type { EventsData } from '@/lib/events';

const eventsFilePath = path.join(process.cwd(), 'src', 'lib', 'events.json');

async function readJsonFile<T>(filePath: string): Promise<T> {
    try {
        const fileContent = await fs.readFile(filePath, 'utf-8');
        return JSON.parse(fileContent);
    } catch (error) {
        if ((error as NodeJS.ErrnoException).code === 'ENOENT') {
            return { events: [] } as any;
        }
        throw error;
    }
}

async function writeJsonFile(filePath: string, data: any): Promise<void> {
    await fs.writeFile(filePath, JSON.stringify(data, null, 2));
}

export async function deleteEvent(eventId: string) {
    try {
        const eventsData = await readJsonFile<EventsData>(eventsFilePath);

        const eventExists = eventsData.events.some(event => event.id === eventId);
        if (!eventExists) {
            return { success: false, message: 'Event not found.' };
        }

        eventsData.events = eventsData.events.filter(event => event.id !== eventId);

        await writeJsonFile(eventsFilePath, eventsData);

        revalidatePath('/admin/events');
        revalidatePath('/events'); 

        return { success: true, message: 'Event deleted successfully.' };
    } catch (error) {
        console.error('Error deleting event:', error);
        return { success: false, message: 'Failed to delete event.' };
    }
}

src/app/admin/events/delete-event-dialog.content:
'use client';

import { useState } from 'react';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Button } from '@/components/ui/button';
import { DropdownMenuItem } from '@/components/ui/dropdown-menu';
import { Loader2, Trash2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useRouter } from 'next/navigation';
